ruleContent.replaceRegex:
@js:
var f=String(src).match(/url\('([^']+)'\) format\('truetype'\)/);
if (f) {
    var f1 = java.queryTTF("http:"+f[1])
    var f2 = java.queryBase64TTF("")
    java.replaceFont(result, f1, f2)
}else{
    result
}

ruleToc.chapterList:
@js:
JSON.parse(result).data.chapter.chapterInfo.map(o=>{
  var referer = JSON.stringify({"Referer": "https://boxnovel.baidu.com/boxnovel/content?gid="+ o.book_id + "&cid="+ o.chapter_id});
  var option = JSON.stringify({"headers":referer});
  return { 
    title: o.chapter_title,     
    link:'https://boxnovel.baidu.com/boxnovel/wiseapi/chapterContent?bookid='+ o.book_id +'&cid='+ o.chapter_id + ','+ option,
    vip: o.price_status
   } 
});

